query getOrganicListing(
    $input: RLPInput!
    $includeCarousels: Boolean!
    $includeSwimlanes: Boolean!
    $includeJoker: Boolean!
    $includeDynamicSearchbarConfig: Boolean!
) {
    rlp(params: $input) {
        organic_listing {
            views {
                returned_count
                available_count
                aggregations {
                    ...Aggregations
                }
                items {
                    ...Vendor
                }
                events {
                    ...VendorEvent
                }
                # ======================= EXCLUDED FIELDS ======================== #
                #banner: String!
                #missing_vendor_reasons: Map
                #partner: OrganicListingPartner
                #tags: Map!
            }
        }

        carousels @include(if: $includeCarousels) {
            ...Carousels
        }

        swimlanes @include(if: $includeSwimlanes) {
            ...SwimlaneList
        }

        joker_offers @include(if: $includeJoker) {
            ...JokerOffer
        }

        dynamic_searchbar_config
            @include(if: $includeDynamicSearchbarConfig) {
            ...DynamicSearchbarConfig
        }

        experiments {
            name
            value
        }

        errors {
            organic_listing
            swimlanes
            joker_offers
            dynamic_searchbar_config
        }
    }
}

fragment VendorEvent on VendorEvent {
    id
    messages
}

fragment Aggregations on OrganicListingAggregation {
    cuisines {
        ...AggregateCharacteristicFields
    }
    food_characteristics {
        ...AggregateCharacteristicFields
    }
    quick_filters {
        ...AggregateCharacteristicFields
    }
    discount_labels {
        title
        count
    }
    delivery_providers {
        id
        count
    }
    partners {
        id
        title
        count
        image_url
        logo_url
    }
    payment_types {
        id
        title
        count
        highlighted
    }
    close_reasons #(Diff structure) need to test!

    # ======================= EXCLUDED FIELDS ======================== #
    #events: [VendorEvent]!   On PARENT LEVEL
    #banner: String!
    #discounts: [OrganicListingDiscount]!
    #food_characteristics_types: Map!
}

fragment AggregateCharacteristicFields on OrganicListingAggregate {
    id
    title
    count
}

fragment VendorMetadata on VendorMetadata {
    available_in
    timezone
    close_reasons
    is_delivery_available
    is_pickup_available
    is_flood_feature_closed
    is_temporary_closed
    has_discount
    events {
        ...VendorEvent
    }
    # ======================= EXCLUDED FIELDS ======================== #
    # name
    # tags
    # actions
}

