    budget
    characteristics {
        cuisines {
            id
            name
            url_key
        }
        food_characteristics {
            id
            name
        }
        primary_cuisine {
            id
            name
            url_key
        }
    }
    accepts_instructions
    delivery_provider
    customer_type
    is_active
    is_preorder_enabled
    is_best_in_city
    tag
    name
    rating
    review_number
    address_line2
    latitude
    longitude
    minimum_order_amount
    minimum_delivery_fee
    minimum_delivery_time
    original_delivery_fee
    delivery_fee_delta
    free_delivery_label
    pickup_duration_range {
        lower_limit_in_minutes
        upper_limit_in_minutes
    }
    delivery_duration_range {
        lower_limit_in_minutes
        upper_limit_in_minutes
    }
    minimum_pickup_time
    is_vat_visible
    is_vat_included_in_product_price
    hero_listing_image
    distance
    has_delivery_provider
    loyalty_program_enabled
    loyalty_percentage_amount
    vertical
    is_premium
    ncr_pricing_model
    ncr_token
    preorder_time_offset
    partner_ids
    metadata {
        ...VendorMetadata
    }
    tags {
        code
        text
        origin
        label_metadata {
            panda_pro {
                is_pro
                type
                is_applicable
            }
        }
    }
    tile {
        type
        primary_tags {
            ...ElementGroup
        }
        secondary_tags {
            ...ElementGroup
        }
        vendor_info {
            ...ElementGroup
        }
    }
    chain {
        code
        name
    }
    discounts_info {
        id
        value
    }
    location_event {
        id
        message
        tags
        type
        value
        name
    }
    favorite_data {
        favorited_on
    }
    url_key
    minimum_basket_value_discount {
        delivery_discount
        is_free_delivery
        threshold
        is_pro
        total_delivery_fee
    }

    # ======================= EXCLUDED FIELDS ======================== #
    # schedules          Vendor info from rdp
    # specialDays        Vendor info from rdp
    # web_path           Share on rdp
    # customer_phone     Vendor contact on verticals
    # budget             Search domain model
    # primary_cuisine_id Search domain model & Redundant
    # characteristics    Redundant information
    # vertical_segment   Catering
    # vertical_type_ids  Megamarts
    # tag
    # redirection_url
    # maximum_express_order_amount
    # is_vat_included_in_product_price
    # allergens_link
    # is_best_in_city
    # accepts_instructions
}

