#menus                               RDP
#toppings                            RDP
#products                            Search
#search_metadata                     Search
#review_with_comment_number          DINE_IN
#is_vat_included & other vat fields  Cart
#trade_register_number               Checkout - payment
#time_picker                         Megamart
#imprint                             Shops
#topic_ratings                       Vendor Scoring/reviews Verticals



fragment CarouselItem on Campaign {
    campaign_id
    active
    ranking
    title
    subtitle
    info
    expedition_types
    vertical_types
    image_small
    image_medium
    image_large
    image_small_desktop
    image_large_desktop
    url_key
    terms
    scopes

    filters {
        budgets
        has_free_delivery
        has_online_payment
        has_discount
        is_voucher_enabled
        tag_id
        tags
        cuisine {
            id
            title
        }
        food_characteristic {
            title
            id
        }
    }
    links {
        web_link
    }
    voucher {
        voucher_code
        is_voucher_saved
    }
    external_integration
}


fragment Carousels on Carousels {
    data {
        id
        campaigns {
            ...CarouselItem
        }
    }
}



fragment Swimlane on Swimlane {
    id
    content_type
    custom_strategy
    filters {
        id
        title
        type
        count
    }
    headline
    layout
    traces
    custom_meta {
        custom_layout
        custom_fields {
            value
            name
        }
    }
    vendors {
        vendor {
            ...Vendor
        }
    }
}

fragment SwimlaneList on Swimlanes {
    data {
        items {
            ...Swimlane
        }
    }
    meta {
        config_name
        took
        traces
    }
    request_id
    status
}



fragment JokerVendor on Vendor {
    id
    city {
        name
    }
    code
    name
    delivery_fee_type
    delivery_duration_range {
        lower_limit_in_minutes
        upper_limit_in_minutes
    }
    distance
    minimum_delivery_time
    minimum_pickup_time
    rating
    review_number
    hero_listing_image
    budget
    minimum_delivery_fee
    minimum_order_amount
    primary_cuisine_id
    pickup_duration_range {
        lower_limit_in_minutes
        upper_limit_in_minutes
    }
    cuisines {
        id
        name
        url_key
    }
    url_key
    is_active
    food_characteristics {
        id
        name
    }
}

fragment JokerOffer on JokerOffers {
    offer_id
    currency
    status
    status_text
    items {
        reservation_code
        is_last_promotion
        rank
        status
        status_text
        vendor {
            code
            details {
                ...JokerVendor
            }
        }
    }
    remaining_duration
    creation_date
    expiration_date
    tiers {
        discount {
            value
            maximum_amount
        }
        tier_id
        mov
    }
    current_tier_id
    next_tier_id
    amount_to_reach_next_tier
    joker_fee
    joker_voucher_code
    joker_commission_base
    tiers_type
}



fragment DynamicSearchbarConfigData on DynamicSearchBarConfigData {
    type
    message
}


fragment DynamicSearchbarConfig on DynamicSearchBarConfig {
    data {
        ...DynamicSearchbarConfigData
    }
}


fragment ElementGroup on ElementGroup {
    id
    elements {
        __typename
        ... on Icon {
            icon_id
            icon_decorators
        }
        ... on Image {
            url
        }
        ... on Separator {
            separator_id
        }
        ... on Text {
            text_id
            text
            args
            text_decorators
        }
    }
}
